#Soma personagens 169
# MAPA DE BITS DOS PERSONAGENS:


# endereco fim do cenario 0x10010000 + 65536

# VETORES COM PARAMETROS DOS PERSONAGENS

# Ordem dos parametros do vetor:
# base, altura, x inicial, y inicial, x final, y, final, offset inicio, variavel

# vetor 1: endereco 0x10010000 + 66412 #KONG
.word 7 10 0 0 10 10 65536 5
# vetor 2: endereco 0x10010000 + 66444 #PRINCESAASD
.word 5 10 25 0 25 5 65816 25 
# vetor 3: endereco 0x10010000 + 66476 
.word 5 10 60 50 80 50 66016 0 
# vetor 4: endereco 0x10010000 + 66508 #BARRIL
.word 7 7 80 15 15 50 66216 80  

# ------------------------------------------------------------

# PROGRAMA                                                                                                        
                                                                                                                                                                                                                                                                                                                                    
.text 

main:

# Parametros globais:
	
	addi $25 $0 128 # quantidade de colunas do cenario
	addi $30 $0 64 # quantidade de linhas do cenario
	mul $26 $25 $30 # area do cenario
	addi $27 $0 251 # assets + vetor 
	addi $6 $0 512 # width do display
	addi $7 $0 4 # porporcao width do display
	
# Chamada de funcao para guardar cenario, personagens e vetores na memoria
	add $10, $27, $26 # contador da iteracao
	add $8 $0 $10
	sll $8 $8 2
	addi $8 $8 0x10010000
	addi $8 $8 -4 # local a ser lido
	add $24 $0 $10
	sll $24 $24 2
	add $24 $24 $8 
	sll $1 $27 2
	sub $24 $24 $1 # local a ser armazenado
	jal store
	
# Chamada de funcao para carregar cenario na tela
	lui $8, 0x1001 # local a ser escrito
	add $24 $0 $26
	sll $24 $24 2
	add $24 $24 $8 # local a ser lido
	addi $10, $0, 0 # contador
	jal load
	
# Ponteiros para vetores dos personagens 
	lui $2 0x1001
	addi $1 $0  66476 # personagem1[0] Kong
	add $2 $2 $1
	
	lui $3 0x1001
	addi $1 $0 66444 # personagem2[0] Princesa
	add $3 $3 $1
	
	lui $4 0x1001
	addi $1 $0 66412 # personagem3[0] Mario
	add $4 $4 $1
	
	lui $5 0x1001
	addi $1 $0 66508 # personagem4[0] Barril
	add $5 $5 $1
	
# loop da animacao da tela:
	# desenha todos personagens
	# aguarda um tempo
	# apaga todos os personagens 
	# controla movimento dos personagens
animacao:
	bne  $0, $0, fimanimacao # comparacao de posicao
	
# carregamento de parametros do personagem 1: protagonista
	lw $22 0($2) # base  
	lw $23 4($2) # altura 
	lw $18 8($2) # x inicial
	lw $19 12($2) # y inicial
	lw $20 16($2) # x final
	lw $21 20($2) # y final
	lw $28 24($2) # inicio 
	lw $29 28($2) # extra
	
# chamada de funcao para carregar personagem na tela 
	addi $8 $0 0x10010000
	add $8 $8 $28 # endereco do inicio do personagem na memoria
	add $9, $0, $22 # base (colunas do desenho)
	add $10, $0, $23 # altura (linhas do desenho)
	add $11, $0, $18 # x
	add $12, $0, $19 # y
	jal personagem
	
# carregamento de parametros de personagem 2: npc
	lw $22 0($3) # base  
	lw $23 4($3) # altura 
	lw $18 8($3) # x inicial
	lw $19 12($3) # y inicial
	lw $20 16($3) # x final
	lw $21 20($3) # y final
	lw $28 24($3) # inicio
	lw $29 28($3) # extra
	
