# funcao para carregar cenario na tela
# paramentros:
	# local a ler $24
	# local a escrever $8
	# contador $10
load:	
	lw $9, 0($24)
	sw $9, 0($8)
	addi $8, $8, 4
	addi $24, $24, 4
	addi $10, $10, 1
	bne $10, $26, load
	jr $31
	
# funcao para guardar cenario e personagem na memoria
# paramentros:
	# local a ser lido $8
	# local a ser armazenado $24
	# contador $10
store:
	lw $9, 0($8)
	sw $9, 0($24)
	addi $8, $8, -4
	addi $24, $24, -4
	addi $10, $10, -1
	bne $10, 0, store
	jr $31

# funcao para desenhar personagem na tela
# parametros:
	# endereco inicial: $8 
	# Largura do personagem: $9
	# altura do personagem: $10
	# Saber o x: $11
	# Saber o y: $12
personagem:  
	lui $15, 0x1001
	sll $11, $11, 2 # enderecos a percorrer no eixo x
	div $6 $7 # width / proporcao
	mflo $1
	sll $1 $1 2 # enderecos a percorrer no eixo y
	mul $12, $12, $1 
	add $15, $15, $11
	add $15, $15, $12 # posicao inicial a ser desenhada
	add $13, $0, $9 # quantidade de colunas
laco1:	beq $10, $0, fimlaco1
laco2:	beq $9, $0, fimlaco2
	lw $14, 0($8) # local a ler
	sw $14, 0($15) # local a desenhar.
	addi $8, $8, 4 
	addi $15, $15, 4
	addi $9, $9, -1 # colunas--
	j laco2
fimlaco2:   
	div $6 $7 # width / proporcao
	mflo $1
	sll $1 $1 2 # enderecos a percorrer no eixo y
	add $15, $15, $1
	sll $16, $13, 2 
	sub $15, $15, $16 # volta para a coluna inicial
	add $9, $0, $13 # valor inicial de colunas 
	addi $10, $10, -1 # linhas--  
	j laco1  
fimlaco1: 
	jr $31
            
# funcao para carregar parte do cenario.
# parametros: 
	# Largura do personagem: $9
	# altura do personagem: $10
	# Saber o x: $11
	# Saber o y: $12
carregafundo:  
	lui $15, 0x1001
	sll $11, $11, 2 # enderecos a percorrer no eixo x
	div $6 $7 # width / proporcao
	mflo $1
	sll $1 $1 2 # enderecos a percorrer no eixo y
	mul $12, $12, $1 
	add $15, $15, $11
	add $15, $15, $12 # posicao inicial a ser desenhada
	add $13, $0, $9 # quantidade de colunas        
	lui $8, 0x1001 
	add $8, $8, $11
	add $8, $8, $12 # posicao inicial a ser desenhada
	add $12, $0, $26
	sll $12, $12, 2
	add $8, $8, $12
	    
laco1.1:
	beq $10, $0, fimlaco1.1
laco2.1:
	beq $9, $0, fimlaco2.1
	lw $14, 0($8) # local a ler
	sw $14, 0($15) # local a desenhar.
	addi $8, $8, 4 
	addi $15, $15, 4
	addi $9, $9, -1 # colunas--
	j laco2.1
fimlaco2.1:    
	div $6 $7 # width / proporcao
	mflo $1
	sll $1 $1 2 # enderecos a percorrer no eixo y
	add $15, $15, $1
	add $8, $8, $1
	sll $16, $13, 2 
	sub $8, $8, $16 # volta para a coluna inicial
	sub $15, $15, $16 
	add $9, $0, $13 # valor inicial de colunas
	addi $10, $10, -1 # linahs--  
	j laco1.1  
fimlaco1.1: 
	jr $31
	
